		    this.movePreview(browser);
		  }
		}
  }
  else if (event.type=="DOMNodeRemoved")
  {
		if (event.relatedNode==this.tabbrowser.mPanelContainer)
		{
  		var browser = event.target.childNodes[1];
  		this.removePreview(browser);
		}
  }
  else if (event.type=="select")
