data=data.substring(11);
switch (data)
{
  case "content.favicon":
    this._showFavicon = this.preferences.getBoolPref(data);
    this.updateContent();
    break;
  case "content.preview":
    this._showPreview = this.preferences.getBoolPref(data);
    this.updateContent();
    break;
  case "content.close":
    this._showClose = this.preferences.getBoolPref(data);
    this.updateContent();
    break;
  case "content.security":
    this._showSecurity = this.preferences.getBoolPref(data);
    this.updateContent();
    break;
  case "content.reload":
    this._showReload = this.preferences.getBoolPref(data);
    this.updateContent();
    break;
  case "content.throbber":
    this._showThrobber = this.preferences.getBoolPref(data);
    this.updateContent();
    break;
  case "content.title":
    this._showTitle = this.preferences.getBoolPref(data);
    this.updateContent();
    break;
  case "content.uri":
    this._showURI = this.preferences.getBoolPref(data);
    this.updateContent();
    break;

  case "enablelogging":
    this._enableLogging = this.preferences.getBoolPref(data);
    break;
  case "loadrefresh":
    this._loadRefresh = this.preferences.getBoolPref(data);
    break;
  case "loadrefreshrate":
    this._loadRefreshRate = this.preferences.getIntPref(data);
    break;
  case "loaddelay":
    this._loadDelay = this.preferences.getIntPref(data);
    break;
  case "refreshrate":
  case "autorefresh":
    this._refreshTimer.cancel();
    if (this.autoRefresh)
    {
      this._refreshTimer.initWithCallback(this, 
                                          this.refreshRate,
                                          Components.interfaces.nsITimer.TYPE_REPEATING_SLACK)
    }
    break;
  case "drawscrolled":
    this._drawScrolled = this.preferences.getBoolPref(data);
    this.redraw();
    break;
}

handlers.handler:
if (event.button == 2)
{
  var x = this.boxObject.screenX-this.window.document.documentElement.boxObject.screenX+event.clientX;
  var y = this.boxObject.screenY-this.window.document.documentElement.boxObject.screenY+event.clientY;

  var tab = null;
  if (event.target.localName == "tabpreview")
  {
    tab = event.target.tab;
  }
  else
  {
    tab = this._selected.tab;
  }
  this.tabbrowser.ownerDocument.popupNode = tab;
  var context = this.tabbrowser.ownerDocument.getAnonymousElementByAttribute(this.tabbrowser,"anonid","tabContextMenu");
  context.showPopup(tab,x,y,"context");
}


if (event.button==0 && (event.target.localName=="separator" || event.target == this))
