}
else
{
  if (tabbrowser.selectedTab != this.tab)
  {
    var focus = false;
    if (document.commandDispatcher.focusedElement && document.commandDispatcher.focusedElement.parentNode==this.parentNode)
    {
      focus=true;
      this.tab.focus();
    }
    tabbrowser.selectedTab=this.tab;
    if (focus)
      this.focus();
  }
  else
  {
    this.focus();
  }
}
